mapper.select:
select r.*, u.realname, u.id_type, u.id_no, u.license_type, u.license_no, u.head_url from tp_edu_record r
inner join tp_user u on r.user_id = u.id
where r.id = #{id}

select * from tp_edu_record 
where user_id = #{userId} 
and batch_num = #{batchNum} 
and edu_type = #{eduType}

select qr.*, u.id_type, u.id_no, u.license_no from tp_edu_record qr
	inner join tp_user u on u.id = qr.user_id


			qr.user_id = #{userId}


			and u.realname like CONCAT('%',#{realname},'%')


			and u.phone like CONCAT('%',#{phone},'%')


			and qr.create_time >= #{createTimeBegin}


			and qr.create_time <= #{createTimeEnd}


	order by create_time desc

select 
	(
		select sum(cost_time) from tp_question_record
		where user_id = #{userId} 
		and batch_num = #{batchNum} 
		and edu_type = #{eduType}
	) + (
		select sum(completed_duration) from tp_video_record
		where user_id = #{userId} 
		and batch_num = #{batchNum} 
	)
