mapper.update:
update tp_user


		id_type = #{idType}


		id_no = #{idNo}


		id_card_img_url = #{idCardImgUrl}


		license_type = #{licenseType}


		license_no = #{licenseNo}


		license_begin_date = #{licenseBeginDate}


		license_end_date = #{licenseEndDate}


		head_url = #{headUrl}


		audit_state = #{auditState}


		update_time = #{updateTime}


where phone = #{phone}

mapper.select:
select * from tp_user 


		id_no = #{idNo}


		or license_no = #{licenseNo}


		or phone = #{phone}

select * from tp_user


		id = #{id}


		and phone like CONCAT('%',#{phone},'%')


		and audit_state = #{auditState}


		and id_type = #{idType}


		and id_no like CONCAT('%',#{idNo},'%')


		and license_type = #{licenseType}


		and license_no like CONCAT('%',#{licenseNo},'%')


		and create_time >= #{createTimeBegin}


		and create_time <= #{createTimeEnd}


		and audit_time >= #{auditTimeBegin}


		and audit_time <= #{auditTimeEnd}


order by create_time desc
